// Get the input data
const inputData = $input.first();

// Try different possible locations of the image data
let imageBase64;
let imageType = 'image/png'; // Default
let imageName = 'image.png'; // Default

// Check if data is in webhook body directly
if (inputData.json && inputData.json.body && inputData.json.body.imageBase64) {
  imageBase64 = inputData.json.body.imageBase64;
  imageType = inputData.json.body.imageType || imageType;
  imageName = inputData.json.body.imageName || imageName;
}
// Check if data is directly in the json object
else if (inputData.json && inputData.json.imageBase64) {
  imageBase64 = inputData.json.imageBase64;
  imageType = inputData.json.imageType || imageType;
  imageName = inputData.json.imageName || imageName;
} 
// Check if we need to use the expression you mentioned
else {
  try {
    // Try to get data using your expression
    const webhookData = $('Webhook').first();
    if (webhookData && webhookData.json && webhookData.json.body) {
      imageBase64 = webhookData.json.body.imageBase64;
      imageType = webhookData.json.body.imageType || imageType;
      imageName = webhookData.json.body.imageName || imageName;
    }
  } catch (error) {
    console.log('Error getting webhook data:', error.message);
  }
}

// Log what we found to help debug
console.log('Image data found:', !!imageBase64);

// If we have image data, return it properly formatted
if (imageBase64) {
  return {
    json: {
      hasImage: true,
      imageType: imageType,
      imageName: imageName
    },
    binary: {
      image: {
        data: imageBase64,
        mimeType: imageType,
        fileName: imageName
      }
    }
  };
}

